import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom'
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from './axios/axios-folders'


export default class App extends Component { 
  state = {
    folders: [],
  }

  //

  createFolder(name) {
    return {
      name
    }
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  async componentDidMount() {
    try {      
      const responseFolders = await Axios.get('/folders.json')
      const folders = []
      Object.values(responseFolders.data).forEach((folder) => {
        folders.push(folder)
      })  
      this.setState({
        folders
      }) 

    } catch (e) {
      console.log(e)
    }
  }

	render() {
		return (
			<div className="App">
        <Router>
          <div className='row'>
            <div className='col-2'>
              <Menu 
                folders={this.state.folders} 
                addFolder={this.addFolder}
                deleteFolder={this.deleteFolder}
              />
            </div>
            <div className='col-10'>
              <Route path='/:id' render={({ match }) => <Main folderId={match.params.id} key={match.params.id} />} />
            </div>
          </div>
        </Router>
			</div>
		);
	}
}
