import React, {Component} from 'react'
import './CardTitle.scss'

export default class CardTitle extends Component {
    class

    state = {
        h1: 'Hello',
        h5: 'World'
    }

    onLabelChange = (e) => {
        this.setState({
            label: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.props.onItemAdded(this.state.label);
        this.setState({
            label: ''
        })
    };


    render() {
        return (
            <React.Fragment>
                <input type='text' placeholder='Заголовок' className='h1'/>
                <h1>{this.state.h1}</h1>
                <input type='text' placeholder='Примечание' className='h5' />
                <h5>{this.state.h5}</h5>
            </React.Fragment>
        )
    }
}
