// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/AddCard/AddCard'
import Axios from 'axios'

export default class Main extends Component{
    maxId = 1000

    state = {
        cards: [
            this.createCard(this.maxId++)
        ]
    }

    createTask(name) {
        return {
            name,
            important: false,
            done: false,
            id: this.maxId++
        }
    }

    createCard() {
        return {
            title: '',
            subtitle: '',
            id: this.maxId++,
            tasks: []
        }
    }

    addCard = (id) => {
        const newCard = this.createCard(id);
        this.setState(({ cards }) => {
            const newArr = [
                ...cards,
                newCard
            ];
            return {
                cards: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/cards.json', newCard)
    };

    addTask = (text) => {
        const newItem = this.createTodoItem(text);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newItem
            ];

            return {
                items: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/test.json', newItem)
    };


    async componentDidMount() {
        try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/cards.json')
            const cards = []

            Object.values(response.data).forEach((key) => {
                cards.push(this.createCard(key.tasks))
            })

            this.setState({
                cards
            })

        } catch (e) {
            console.log(e)
        }
    }  


    render() {
        return (
            <div className='Main'>
                {this.state.cards.map((card) => {
                    return(
                        <Card tasks={card.tasks} addTask={this.addTask} />
                    )
                })

                }
                <AddCard addCard={this.addCard} />
            </div>
        )
    }
}
